src/App.js.content:
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import HomeScreen from './screens/HomeScreen';
import PlayerScreen from './screens/PlayerScreen';
import './index.css';

function App() {
  return (
    <div className="app">
      <Router>
        <Routes>
          <Route path="/" element={<HomeScreen />} />
          <Route path="/player" element={<PlayerScreen />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;

src/index.css.content:
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  margin: 0;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
    'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
    sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  background-color: #0A0A0A;
  color: #FFFFFF;
  height: 100vh;
  overflow-x: hidden;
}

#root {
  height: 100vh;
  display: flex;
  flex-direction: column;
}

/* App Container */
.app {
  height: 100vh;
  background-color: #0A0A0A;
  display: flex;
  flex-direction: column;
}

/* Header */
.header {
  background-color: #0A0A0A;
  border-bottom: 1px solid #2A2A2A;
  padding: 15px 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  min-height: 60px;
}

.header-title {
  font-size: 24px;
  font-weight: bold;
  color: #9C4DCC;
  margin: 0;
}

.header-back {
  background: none;
  border: none;
  color: #FFFFFF;
  font-size: 18px;
  cursor: pointer;
  padding: 8px;
  border-radius: 4px;
}

.header-back:hover {
  background-color: #2A2A2A;
}

.header-subtitle {
  font-size: 18px;
  font-weight: 600;
  color: #FFFFFF;
  margin: 0;
}

/* Home Screen */
.home-container {
  flex: 1;
  background-color: #0A0A0A;
  display: flex;
  flex-direction: column;
}

.home-header {
  padding: 15px 20px;
  border-bottom: 1px solid #2A2A2A;
}

.home-subtitle {
  font-size: 16px;
  color: #CCCCCC;
  font-weight: 500;
  margin: 0;
}

.song-list {
  flex: 1;
  overflow-y: auto;
  padding: 10px 0;
}

/* Song Item */
.song-item {
  display: flex;
  align-items: center;
  padding: 12px 20px;
  background-color: #0A0A0A;
  border-bottom: 1px solid #1A1A1A;
  cursor: pointer;
  transition: background-color 0.2s;
}

.song-item:hover {
  background-color: #1A1A1A;
}

.song-cover {
  width: 60px;
  height: 60px;
  border-radius: 8px;
  background-color: #2A2A2A;
  display: flex;
  justify-content: center;
  align-items: center;
  margin-right: 15px;
  font-size: 24px;
}

.song-text {
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.song-title {
  font-size: 16px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 4px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.song-artist {
  font-size: 14px;
  color: #CCCCCC;
  font-weight: 400;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.song-play-indicator {
  width: 30px;
  height: 30px;
  display: flex;
  justify-content: center;
  align-items: center;
}

.song-play-icon {
  font-size: 16px;
  color: #9C4DCC;
  font-weight: bold;
}

/* Player Screen */
.player-container {
  flex: 1;
  background-color: #0A0A0A;
  padding: 30px;
  display: flex;
  flex-direction: column;
  height: 100vh;
  overflow: hidden;
}

.player-cover-section {
  flex: 2;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 40px 0;
}

.player-cover {
  width: 280px;
  height: 280px;
  border-radius: 20px;
  background-color: #2A2A2A;
  display: flex;
  justify-content: center;
  align-items: center;
  box-shadow: 0 10px 20px rgba(156, 77, 204, 0.3);
  font-size: 120px;
}

.player-info {
  text-align: center;
  padding: 20px 0;
}

.player-title {
  font-size: 28px;
  font-weight: bold;
  color: #FFFFFF;
  margin: 0 0 8px 0;
}

.player-artist {
  font-size: 18px;
  color: #CCCCCC;
  margin: 0;
}

.player-progress {
  padding: 30px 0;
}

.progress-bar {
  height: 4px;
  background-color: #2A2A2A;
  border-radius: 2px;
  margin-bottom: 10px;
  position: relative;
  cursor: pointer;
}

.progress-fill {
  height: 100%;
  background-color: #9C4DCC;
  border-radius: 2px;
  transition: width 0.1s;
}

.progress-thumb {
  position: absolute;
  top: -6px;
  width: 16px;
  height: 16px;
  background-color: #9C4DCC;
  border-radius: 8px;
  margin-left: -8px;
  cursor: pointer;
}

.progress-time {
  display: flex;
  justify-content: space-between;
}

.time-text {
  font-size: 14px;
  color: #CCCCCC;
}

.player-controls {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px 0;
  gap: 30px;
}

.control-button {
  background: none;
  border: none;
  color: #FFFFFF;
  cursor: pointer;
  padding: 15px;
  border-radius: 50%;
  transition: background-color 0.2s;
  font-size: 32px;
}

.control-button:hover {
  background-color: #2A2A2A;
}

.play-button {
  width: 80px;
  height: 80px;
  background-color: #9C4DCC;
  border: none;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  box-shadow: 0 5px 10px rgba(156, 77, 204, 0.4);
  transition: all 0.2s;
  font-size: 36px;
  color: #FFFFFF;
}

.play-button:hover {
  transform: scale(1.05);
  box-shadow: 0 8px 15px rgba(156, 77, 204, 0.6);
}

.player-bottom {
  display: flex;
  justify-content: space-around;
  align-items: center;
  padding: 20px 0;
}

.icon-button {
  background: none;
  border: none;
  color: #CCCCCC;
  cursor: pointer;
  padding: 10px;
  border-radius: 50%;
  transition: all 0.2s;
  font-size: 24px;
}

.icon-button:hover {
  color: #9C4DCC;
  background-color: #2A2A2A;
}

/* Responsive */
@media (max-width: 768px) {
  .player-container {
    padding: 20px;
  }

  .player-cover {
    width: 220px;
    height: 220px;
    font-size: 100px;
  }

  .player-title {
    font-size: 24px;
  }

  .player-artist {
    font-size: 16px;
  }
}

