  font-size: 28px;
  font-weight: 700;
  margin-bottom: 4px;
}

.period {
  color: #fff;
  font-size: 14px;
  opacity: 0.9;
}

.premium-features {
  text-align: left;
  margin: 24px 0;
}

.feature {
  color: #1DB954;
  margin: 8px 0;
  display: flex;
  align-items: center;
  font-size: 14px;
}

.feature::before {
