  color: #fff;
  border: none;
  padding: 10px 20px;
  border-radius: 20px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
}

.edit-profile-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(29, 185, 84, 0.3);
}

.profile-content {
  display: flex;
  flex-direction: column;
  gap: 30px;
}

/* Profile Picture Section */
.profile-picture-section {
  display: flex;
  justify-content: center;
  margin-bottom: 20px;
}

