}

.camera-icon {
  font-size: 30px;
  margin-bottom: 5px;
}

.upload-text {
  font-size: 12px;
  color: #fff;
  text-align: center;
}

/* Profile Information */
.profile-info-section {
  background: #1a1a1a;
  border-radius: 16px;
  padding: 24px;
  border: 1px solid #333;
}

.profile-field {
  margin-bottom: 20px;
}

.field-label {
  display: block;
  color: #999;
  font-size: 14px;
  margin-bottom: 8px;
  font-weight: 500;
}

.field-value {
  color: #fff;
  font-size: 16px;
  padding: 12px 0;
  border-bottom: 1px solid #333;
}

.field-input {
  width: 100%;
  background: #2a2a2a;
  border: 1px solid #444;
  border-radius: 8px;
  padding: 12px 16px;
  color: #fff;
  font-size: 16px;
  transition: all 0.2s;
}

.field-input:focus {
  outline: none;
  border-color: #1DB954;
  box-shadow: 0 0 0 2px rgba(29, 185, 84, 0.2);
}

.premium-status {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 12px 0;
  border-bottom: 1px solid #333;
}

.status-badge {
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 600;
  text-transform: uppercase;
}

.status-badge.free {
