  color: #fff;
}

.upgrade-badge {
  background: transparent;
  border: 1px solid #1DB954;
  color: #1DB954;
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
}

.upgrade-badge:hover {
  background: #1DB954;
  color: #fff;
}

/* Profile Actions */
.profile-actions {
  display: flex;
  justify-content: center;
  margin-top: 20px;
}

.save-profile-btn {
  background: linear-gradient(135deg, #1DB954, #1ed760);
  color: #fff;
  border: none;
  padding: 14px 32px;
  border-radius: 25px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
}

.save-profile-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(29, 185, 84, 0.3);
}

/* Stats Section */
.profile-stats {
  background: #1a1a1a;
  border-radius: 16px;
  padding: 24px;
  border: 1px solid #333;
}

.stats-title {
  color: #fff;
  margin: 0 0 20px 0;
  font-size: 20px;
  font-weight: 600;
}

.stats-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
  gap: 20px;
}

.stat-item {
  text-align: center;
  padding: 16px;
  background: #2a2a2a;
  border-radius: 12px;
  transition: all 0.2s;
}

.stat-item:hover {
  background: #333;
  transform: translateY(-2px);
}

.stat-number {
  color: #1DB954;
  font-size: 28px;
  font-weight: 700;
  margin-bottom: 4px;
}

.stat-label {
  color: #999;
  font-size: 14px;
}

/* Responsive Design */
@media (max-width: 768px) {
  .view-profile-screen {
    padding: 16px;
  }

  .profile-header {
    flex-direction: column;
    gap: 16px;
    text-align: center;
  }

  .profile-picture-container {
    width: 120px;
    height: 120px;
  }

  .profile-icon-large {
    font-size: 48px;
  }

  .stats-grid {
    grid-template-columns: repeat(3, 1fr);
    gap: 16px;
  }

  .stat-number {
    font-size: 24px;
  }
}

