import { supabase } from '../supabaseClient';

const MUSIC_BUCKET = 'Music';
const COVERS_BUCKET = 'Covers';
const PUBLIC_BASE = (path) => `https://ofkfygqrfenctzitigae.supabase.co/storage/v1/object/public/${path}`;

function stripExt(name){
  return name.replace(/\.[^.]+$/,'');
}

export async function loadMusicLibrary() {
  console.log('üéµ Loading music library from Supabase...');

  // Direct list of known files (exactly from user's screenshots)
  const knownSongs = [
    'deepabstractambient',
    'retro-lounge', 
    'running-night',
    'vlog-beat-background',
    'apocalypse-1-original-lyrics',
    '8039s-nostalgia',
    'retro-80s-sax',
    '80s-baby-original-lyrics',
    'apocalypse-original-lyrics',
    'lady-of-the-80',
    '80-cinematic-synthwave'
  ];

  // Format song titles nicely
  const formatTitle = (filename) => {
    return filename
      .replace(/[-_]/g, ' ')
      .replace(/\b\w/g, l => l.toUpperCase())
      .replace(/80s/gi, '80s')
      .replace(/1 /g, '1: ');
  };

  // Create songs array using known files
  const songs = knownSongs.map(baseName => {
    const musicUrl = PUBLIC_BASE(`${MUSIC_BUCKET}/${baseName}.mp3`);
    const coverUrl = PUBLIC_BASE(`${COVERS_BUCKET}/${baseName}.png`);

    return {
      id: baseName,
      title: formatTitle(baseName),
      url: musicUrl,
      cover: coverUrl
    };
  });

  console.log('üéµ Created songs count:', songs.length);
  console.log('üéµ Sample song URLs (FIXED):', {
    first: songs[0]?.url,
    cover: songs[0]?.cover
  });
  return songs;
}

src/services/musicLibrary.js.content:
import { supabase } from '../supabaseClient';

// Egyszer≈± (nem rekurz√≠v) list√°z√°s a bucket gy√∂ker√©ben ‚Äì jelen ig√©nyhez el√©g.
async function listAllFiles(bucket, { extensions = null, limit = 1000 } = {}) {
  const { data, error } = await supabase.storage.from(bucket).list('', {
    limit,
    sortBy: { column: 'name', order: 'asc' }
  });
  if (error) throw new Error(`Gre≈°ka pri listanju bucket-a ${bucket}: ${error.message}`);
  if (!data) return [];
  return data
    .filter(e => e && e.name)
    .map(e => e.name)
    .filter(name => {
      if (!extensions) return true;
      const ext = name.split('.').pop().toLowerCase();
      return extensions.includes(ext);
    });
}

function baseName(filename) {
  return filename.replace(/\.[^/.]+$/, '');
}

function formatTitle(raw) {
  if (/^[A-F0-9]{8,}$/i.test(raw)) return raw; // leave hash-looking IDs
  return raw
    .replace(/[_-]+/g, ' ')
    .trim()
    .replace(/\s+/g, ' ')
    .split(' ')
    .map(w => w.charAt(0).toUpperCase() + w.slice(1))
    .join(' ');
}

export async function fetchMusicLibrary({ includeUnmatched = true, fallbackCover = '/fallback-cover.png', debug = false } = {}) {
  const [musicFiles, coverFiles] = await Promise.all([
    listAllFiles('Music', { extensions: ['mp3'] }),
    listAllFiles('Covers', { extensions: ['png', 'jpg', 'jpeg'] })
  ]);

  if (debug && typeof window !== 'undefined') {
    console.log('[MusicLibrary] Fetched raw lists', { musicFiles, coverFiles });
  }

  const musicMap = new Map(musicFiles.map(f => [baseName(f), f]));
  const coverMap = new Map(coverFiles.map(c => [baseName(c), c]));

  const songs = [];
  // Minden mp3-b√≥l k√©sz√≠t√ºnk rekordot ‚Äì cover optional
  for (const [id, musicFile] of musicMap.entries()) {
    const coverFile = coverMap.get(id);
    const { data: { publicUrl: musicUrl } } = supabase.storage.from('Music').getPublicUrl(musicFile);
    let coverUrl = fallbackCover;
    if (coverFile) {
      coverUrl = supabase.storage.from('Covers').getPublicUrl(coverFile).data.publicUrl;
    }
    songs.push({ title: formatTitle(id), url: musicUrl, cover: coverUrl });
  }

  songs.sort((a, b) => a.title.localeCompare(b.title));
  if (debug && typeof window !== 'undefined') console.log('[MusicLibrary] Final songs list', songs);
  return songs;
}

let _cache = null;
let _cacheTime = 0;
const CACHE_TTL_MS = 60_000; // 1 minute

export async function fetchMusicLibraryCached(force = false, options = {}) {
  const now = Date.now();
  if (!force && _cache && now - _cacheTime < CACHE_TTL_MS) return _cache;
  _cache = await fetchMusicLibrary(options);
  _cacheTime = now;
  return _cache;
}

src/services/likeService.js.content:
import { supabase } from '../supabaseClient';

// Get current user ID (simple mock for now - can be enhanced with real auth)
const getCurrentUserId = () => {
  // For now, use a simple user ID. This can be enhanced with real authentication
  return localStorage.getItem('user_id') || 'demo_user';
};

// Check if song is liked by current user
export const checkSongLiked = async (songId) => {
  try {
    if (!songId) return false;

    const userId = getCurrentUserId();
    if (!userId) return false;

    const { data, error } = await supabase
      .from('liked_songs')
      .select('id')
      .eq('user_id', userId)
      .eq('song_id', songId)
      .maybeSingle();

    if (error) {
      console.error('Error checking if song is liked:', error);
      return false;
    }

    return !!data;
  } catch (error) {
    console.error('Error checking if song is liked:', error);
    return false;
  }
};

// Like a song
export const likeSong = async (song) => {
  try {
    if (!song.id) {
      console.error('Cannot like song: missing song ID');
      return false;
    }

    const userId = getCurrentUserId();
    if (!userId) {
      console.error('Cannot like song: user not logged in');
      return false;
    }

    const { data, error } = await supabase
      .from('liked_songs')
      .upsert({
        user_id: userId,
        song_id: song.id,
        song_title: song.title,
        song_artist: song.artist,
        song_cover: song.cover,
        song_src: song.src
      }, {
        onConflict: 'user_id,song_id'
      });

    if (error) {
      console.error('Error liking song:', error);
      return false;
    }

    console.log('Song liked successfully:', song.title);
    return true;
  } catch (error) {
    console.error('Error liking song:', error);
    return false;
  }
};

// Unlike a song
export const unlikeSong = async (songId) => {
  try {
    const userId = getCurrentUserId();

    const { error } = await supabase
      .from('liked_songs')
      .delete()
      .eq('user_id', userId)
      .eq('song_id', songId);

    if (error) {
      console.error('Error unliking song:', error);
      return false;
    }

    console.log('Song unliked successfully');
    return true;
  } catch (error) {
    console.error('Error unliking song:', error);
    return false;
  }
};

// Get all liked songs for current user
export const getLikedSongs = async () => {
  try {
    const userId = getCurrentUserId();

    const { data, error } = await supabase
      .from('liked_songs')
      .select('*')
      .eq('user_id', userId)
      .order('created_at', { ascending: false });

    if (error) {
      console.error('Error getting liked songs:', error);
      return [];
    }

    return data || [];
  } catch (error) {
    console.error('Error getting liked songs:', error);
    return [];
  }
};

// Set user ID (for simple authentication)
export const setUserId = (userId) => {
  localStorage.setItem('user_id', userId);
};

// Check if user is logged in (for real authentication)
export const isUserLoggedIn = () => {
  const userId = getCurrentUserId();
  // For now, consider demo users as "not logged in" for proper auth flow
  // This can be updated when real authentication is implemented
  return userId && !userId.startsWith('demo_user');
};

